'use strict';
var log = console.log.bind(console);
var dir = console.dir.bind(console);

module.exports = function(args) {
	var script = null;
	var part1 = [];
	var part2 = [];

	for (var i = 0; i < args.length; i ++) {
		var arg = args[i];
		if (script === null) {
			if (/^\-/.test(arg)) {
				part1.push(arg);
			} else {
				script = arg;
			}
		} else {
			part2.push(arg);
		}
	}

	return {
		script: script,
		part1: part1,
		part2: part2
	};
}



